mapper.select:
		select rno, sid, editer, stitle, shits, sdate 
				  from (select rownum rno, sid, editer, stitle, sstitle, to_char(sdate, 'yyyy-mm-dd') sdate, shits 
			            from (select sid, editer, stitle, sstitle, sdate, shits from mg_board_story order by sdate desc)
                        )
